package br.fmp.av2.controller; import br.fmp.av2.dto.AlunoCreateDTO; import br.fmp.av2.dto.AlunoDTO; import br.fmp.av2.service.AlunoService; import jakarta.validation.Valid; import lombok.RequiredArgsConstructor; import org.springframework.http.HttpStatus; import org.springframework.http.ResponseEntity; import org.springframework.web.bind.annotation.*; import java.util.List; @RestController @RequestMapping("/aluno") @RequiredArgsConstructor public class AlunoController { private final AlunoService service; @GetMapping public ResponseEntity<List<AlunoDTO>> findAll() { return ResponseEntity.ok(service.findAll()); } @GetMapping("/{id}") public ResponseEntity<AlunoDTO> findById(@PathVariable Long id) { return ResponseEntity.ok(service.findById(id)); } @PostMapping public ResponseEntity<AlunoDTO> create(@Valid @RequestBody AlunoCreateDTO dto) { AlunoDTO created = service.create(dto); return ResponseEntity.status(HttpStatus.CREATED).body(created); } @PutMapping("/{id}") public ResponseEntity<AlunoDTO> update(@PathVariable Long id, @Valid @RequestBody AlunoCreateDTO dto) { return ResponseEntity.ok(service.update(id, dto)); } @DeleteMapping("/{id}") public ResponseEntity<Void> delete(@PathVariable Long id) { service.delete(id); return ResponseEntity.noContent().build(); } } 